mapper.select:
select 

from j_need
where id = #{needId,jdbcType=BIGINT} and member_id = #{memberId}

SELECT n.id, n.title, n.content, n.member_id, n.company_id, n.attribute, n.status, n.class_id, n.add_time, n.update_time, c.name as className FROM j_need n LEFT JOIN j_class c ON n.class_id = c.id
member_id = #{need.memberId,jdbcType=BIGINT}
limit #{start},#{count};
