mapper.select:
select 

from j_need
where id = #{needId,jdbcType=BIGINT} and member_id = #{memberId}

SELECT n.id, n.title, n.content, n.member_id, n.company_id, n.attribute, n.status, n.class_id, n.add_time, n.update_time, c.name as className FROM j_need n LEFT JOIN j_class c ON n.class_id = c.id
member_id = #{memberId,jdbcType=BIGINT}
AND n.status = #{status,jdbcType=TINYINT}
AND title like CONCAT('%',#{title},'%')

SELECT n.id, n.title, n.content, n.member_id, n.company_id, n.attribute, n.status, n.class_id, n.add_time, n.update_time, n.hits, n.end_time, c.name as className, m.name as memberName, m.mobile, m.address FROM j_need n LEFT JOIN j_class c ON n.class_id = c.id LEFT JOIN j_member m on m.id = n.member_id
member_id = #{memberId,jdbcType=BIGINT}
AND n.status = #{status,jdbcType=TINYINT}
AND title like CONCAT('%',#{title},'%')
AND n.id = #{id}
